CREATE TABLE `handshake`.`daraz_intregations` (`id` INT(10) UNSIGNED NOT NULL AUTO_INCREMENT , `user_id` INT(10) UNSIGNED NOT NULL , `app_key` VARCHAR(255) NULL DEFAULT NULL , `secret_key` VARCHAR(255) NULL DEFAULT NULL , `access_token` VARCHAR(255) NULL DEFAULT NULL , `refresh_token` VARCHAR(255) NULL DEFAULT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;
