CREATE TABLE `handshake`.`daraz_shops` (`id` BIGINT(11) UNSIGNED NOT NULL AUTO_INCREMENT , `user_id` INT(10) UNSIGNED NOT NULL , `shop_name` VARCHAR(255) NOT NULL , `shop_email` VARCHAR(255) NOT NULL , `status` TINYINT(2) NOT NULL DEFAULT '0' , `slug` VARCHAR(255) NULL DEFAULT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;
