CREATE TABLE `handshake`.`packages` (`id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT , `package_name` VARCHAR(255) NOT NULL , `package_price` INT(20) NOT NULL , `product_limit` INT(20) NOT NULL DEFAULT '0' , `warehouse_limit` INT(20) NOT NULL DEFAULT '0' , `daraz_sync_limit` INT(20) NOT NULL DEFAULT '0' , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;
